mapper.select:
        
          
	    t.del_flag = 0
             and t.demo_name like concat('%', #{demoName}, '%')
             and t.demo_time >= #{params.beginDemoTime} and t.demo_time <= #{params.endDemoTime}
             and t.demo_phone = #{demoPhone}
             and t.demo_phone_search = #{demoPhoneSearch}
             and t.demo_content = #{demoContent}
             and t.role_id = #{roleId}
             and sysrole.role_name like concat('%', #{roleName}, '%')
             and t.post_id = #{postId}
             and syspost.post_name like concat('%', #{postName}, '%')
            
             
            	${params.customizeAdvanced} 
            
        
        order by t.update_time desc, t.create_time desc

mapper.update:
update t_demo
demo_name = #{demoName}, demo_time = #{demoTime}, demo_phone = #{demoPhone}, demo_phone_search = #{demoPhoneSearch}, demo_image = #{demoImage}, attachment = #{attachment}, demo_content = #{demoContent}, del_flag = #{delFlag}, role_id = #{roleId}, post_id = #{postId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where demo_id = #{demoId}

mapper.insert:
      insert into t_demo (demo_name, demo_time, demo_phone, demo_phone_search, demo_image, attachment, demo_content, role_id, create_by, create_time)
      values
      
          
				#{item.demoName}, #{item.demoTime}, #{item.demoPhone}, #{item.demoPhoneSearch}, #{item.demoImage}, #{item.attachment}, #{item.demoContent}, #{item.roleId}, #{item.createBy}, #{item.createTime}

mapper.update:
update t_demo

    demo_name = 
    
        WHEN #{item.demoName} THEN #{item.demoName}
    ,
    demo_time = 
    
        WHEN #{item.demoName} THEN #{item.demoTime}
    ,
    demo_image = 
    
        WHEN #{item.demoName} THEN #{item.demoImage}
    ,
    attachment = 
    
        WHEN #{item.demoName} THEN #{item.attachment}
    ,
    demo_content = 
    
        WHEN #{item.demoName} THEN #{item.demoContent}
    ,
    update_by = 
    
        WHEN #{item.demoName} THEN #{item.updateBy}
    ,
    update_time = 
    
        WHEN #{item.demoName} THEN #{item.updateTime}
    
    where demo_name in
    
        #{item.demoName}
