mapper.select:

  
     and title like concat('%', #{title}, '%')
     and sub_title like concat('%', #{subTitle}, '%')
     and type = #{type}
     and set_top = #{setTop}
     and publish_time between #{params.beginPublishTime} and #{params.endPublishTime}
     and status = #{status}
     and disabled = #{disabled}
     and anonymous = #{anonymous}

order by disabled asc, set_top desc, publish_time desc

    	
    	
    		t.status = 2 and t.disabled = 0 
			and
			(
				t.anonymous = 1 or 
				(
					t.anonymous = 0 and exists (select 1 from sys_dept d where d.dept_id = #{loginDeptId} and (d.dept_id = t1.dept_id or find_in_set(t1.dept_id, d.ancestors))) 
				)
			)
			 and t.title like concat('%', #{title}, '%')
             and t.type = #{type}
             and t.publish_time between #{params.beginPublishTime} and #{params.endPublishTime}
    	
    	order by disabled asc, set_top desc, publish_time desc

mapper.update:
update sys_info
title = #{title}, sub_title = #{subTitle}, cover = #{cover}, content = #{content}, type = #{type}, set_top = #{setTop}, anonymous = #{anonymous}, publish_time = #{publishTime}, status = #{status}, disabled = #{disabled}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where info_id = #{infoId}
