mapper.update:
update sys_info
title = #{title}, sub_title = #{subTitle}, cover = #{cover}, summary = #{summary}, content = #{content}, category_id = #{categoryId}, source = #{source}, author = #{author}, set_top = #{setTop}, anonymous = #{anonymous}, publish_time = #{publishTime}, status = #{status}, disabled = #{disabled}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
