mapper.select:

  
     and t.menu_name like concat('%', #{menuName}, '%')
     and t.menu_type = #{menuType}
     and t.status = #{status}
     and t.perms = #{perms}

order by t.parent_id, t.order_num

select distinct t.menu_id, t.menu_name, t.menu_type, t.parent_id, t.order_num, t.page_path, t.icon_path, t.selected_icon_path, t.query_param, t.icon_type, t.icon_size, t.view_class, t.img_src, t.is_frame, t.status, ifnull(t.perms,'') as perms, t.create_by, t.create_time, t.update_by, t.update_time, t.remark from sys_mobile_menu t left join sys_role_mobile_menu rm on t.menu_id = rm.menu_id left join sys_user_role ur on rm.role_id = ur.role_id left join sys_role ro on ur.role_id = ro.role_id where ur.user_id = #{params.userId}
AND t.menu_name like concat('%', #{menuName}, '%')
AND t.status = #{status}
order by t.parent_id, t.order_num

select m.menu_id from sys_mobile_menu m left join sys_role_mobile_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from sys_mobile_menu m inner join sys_role_mobile_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num

mapper.update:
update sys_mobile_menu
menu_name = #{menuName}, menu_type = #{menuType}, parent_id = #{parentId}, order_num = #{orderNum}, page_path = #{pagePath}, icon_path = #{iconPath}, selected_icon_path = #{selectedIconPath}, query_param = #{queryParam}, icon_type = #{iconType}, icon_size = #{iconSize}, view_class = #{viewClass}, img_src = #{imgSrc}, is_frame = #{isFrame}, status = #{status}, perms = #{perms}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where menu_id = #{menuId}
